% Reglas


%tiene_vulnerabilidades(SO) :-
%    sistema_operativo(SO, _),
%    vulnerability(_, _, _, SO, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _).

tiene_vulnerabilidades(SO) :-
    findall([CVE, Fecha, Descripcion],
            vulnerability(_, _, _, SO, _, _, _, _, _, _, CVE, Fecha, _, _, _, _, _, _, _, Descripcion, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _), % 63
            Vulnerabilidades),
    imprimir_vulnerabilidades(Vulnerabilidades).

imprimir_vulnerabilidades([]) :-
    writeln('No se encontraron vulnerabilidades.').
imprimir_vulnerabilidades([[CVE, Fecha, Descripcion] | Resto]) :-
    format('CVE: ~w~nFecha: ~w~nDescripcion: ~w~n~n', [CVE, Fecha, Descripcion]),
    imprimir_vulnerabilidades(Resto).

% detalles_vulnerabilidad(CVE, Fecha, Descripcion) :-
%    vulnerability(_, _, _, SO, _, _, _, _, _, _, _, _, CVE, Fecha, _, _, _, Descripcion, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _).

detalles_vulnerabilidad(CVE) :-
    vulnerability(_, _, _, _, _, _, _, _, _, CVE, _, Fecha, _, _, _, _, _, Descripcion, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _),
    format('CVE: ~w~nFecha: ~w~nDescripcion: ~w~n', [CVE, Fecha, Descripcion]).


sistema_afectado_por_vulnerabilidad(CVE, SO) :-
    vulnerability(_, _, _, SO, _, _, _, _, _, CVE, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _).

severidad_vulnerabilidad(CVE, Severidad) :-
    vulnerability(_, _, _, _, _, _, _, _, _, CVE, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, Severidad, _, _, _, _, _, _, _, _, _, _, _).

vulnerabilidad_por_fecha(Fecha, CVE, Descripcion) :-
    vulnerability(_, _, _, _, _, _, _, _, _, CVE, _, Fecha, _, _, _, _, _, Descripcion, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _).





