% Mostrar todas las vulnerabilidades de acuerdo a un Sistema Operativo

tiene_vulnerabilidades(SO, Vulnerabilidades) :-
    findall([ID, CVE, Fecha, Descripcion, EPSS],
            vulnerability(ID, _, _, SO, _, _, _, _, _, _, CVE, Fecha, _, _, _, _, _, _, _, Descripcion, _, _, _, _, _, _, _, _, _, EPSS, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _),
            Vulnerabilidades).